import React, { useState } from "react";
import axios from "axios";

function App() {
  const [urls, setUrls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [lastShortenedUrl, setLastShortenedUrl] = useState("");

  // Function that makes a request to the backend to get all shortened URLs
  const fetchUrls = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/shorten");
      setUrls(response.data);
      setLastShortenedUrl(response.data.shortUrl);
    } catch (error) {
      console.log(error);
    }
  };

  // Function that makes a request to the backend to shorten URLs
  const shortenUrl = async (url) => {
    try {
      const response = await axios.get.post(
        "http://localhost:3000/api/urls",
        { url }
      );
      setUrls([...urls, response.data]);
      setLastShortenedUrl(response.data.shortUrl);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <ShortenForm onShorten={shortenUrl} />
      <ShortenedInfo lastShortenedUrl={lastShortenedUrl} />
      <ShortenedTable urls={urls} loading={loading} />
    </div>
  );
}

export default App;
